  <div class="wrapper">
    <nav class="navbar navbar-expand-lg navbar-light bg-light">
      <a class="navbar-brand">David Zak's Assignment 3 - Team Details</a>
    </nav>
    <div class="container">
      <div class="row">

        <TeamCard
          v-for="(team, index) in teams"
          :key="`team-${index}`"
          :employees="employees"
          :projects="projects" 
          :team="team"

          @team-updated="showModalWindow" 
        ></TeamCard>

      </div>
    </div>
  </div>

</template>

<style scoped>
.col-sm-4 {
  margin-top: 2em;
}
</style>

<script>
import TeamCard from "./TeamCard.vue";
import { map, pick, filter, shuffle, chunk, sortBy } from "lodash-es";
const teamsApiUrl = "https://nameless-island-49053.herokuapp.com";
export default {
  name: "App",
  data: function() {
    return {
      teams: [],
      employees: [],
      projects: []
    };
  },
  methods: {
    loadTeams: function() {
      fetch(`${teamsApiUrl}/teams`)
        .then(res =>  res.json())
        .then(teams => {
          console.log(teams);
          this.teams = teams;
